const dateConvertor = () => {
  try {
    let date = new Date();
    let month = date.getMonth() + 1; //gives
    let day = date.getDay(); //gives day
    const year = date.getFullYear(); // gives year
    if (month <= 9) {
      month = "0" + month;
    }

    if (day <= 9) {
      day = "0" + day;
    }
    date = month + "/" + day + "/" + year;
    return date;
  } catch (err) {
    console.log(err.msg);
  }
};

module.exports.dateConvertor1 = dateConvertor;
